 function infoTips(strMes,Type){switch(Type){case 3:Metronic.alert({container:"",place:"append", type:"warning", message:strMes, close:true, reset:false, focus:true, closeInSeconds:5, icon:"check"});break;case 2:Metronic.alert({container:"",place:"append", type:"info", message:strMes, close:true, reset:false, focus:true, closeInSeconds:5, icon:"check"});break;case 1:Metronic.alert({container:"",place:"append", type:"success", message:strMes, close:true, reset:false, focus:true, closeInSeconds:5, icon:"check"});break;default:Metronic.alert({container:"", place:"append", type:"danger", message:strMes, close:true, reset:false, focus:true, closeInSeconds:0, icon:"warning"});break;}} 
function getRootPath(t){ var curWwwPath=window.document.location.href; var pathName=window.document.location.pathname;var pos=curWwwPath.indexOf(pathName); var localhostPath=curWwwPath.substring(0,pos); var projectName=pathName.substring(0,pathName.substr(1).indexOf('/')+1);if(t===0){return(localhostPath+projectName);}else{return(localhostPath);}}
function getRootUrl(str){return window.document.location.href.substring(0,window.document.location.href.indexOf(str))+str+'/';}
function today(type){var date=new Date();var a=date.getFullYear();var b=date.getMonth()+1;var c=date.getDate();var d=date.getHours();var e=date.getMinutes();var f=date.getSeconds();var output;switch(type){case 0:output=a+'年'+b+'月'+c+'日';break;case 1:output=a+'-'+b+'-'+c+'- '+d+':'+e+':'+f;break;case 2:output=a+'年'+b+'月'+c+'日'+d+'时'+e+'分'+f+'秒';break;case 3:output=a+'-'+b+'-'+c+'- '+d+':'+e;break;case 4:output=a+'年'+b+'月'+c+'日  '+d+'时'+e+'分';break;case 5:output=b+'/'+c+'/'+a;break;case 6:output=a+'-'+b+'-'+c;break;}
return output;}
function HeadFix(){if($(document.body).outerWidth(true)>480){$(".page-sidebar-wrapper").css("margin-top","-18px");}else{$(".page-sidebar-wrapper").css("margin-top","0px");}} 
function Json2Array(strJson){var iCol=0;var iSingle=[];for(var i=0;i<strJson.rows;i++){iCol=0;iSingle[i]=[]; for(var key in strJson.data[i]){iSingle[i][iCol]=strJson.data[i][key];iCol++;}}
return iSingle;} 
function Json2Head(strJson){var strHead='',strRow;var strDataTitle='title';for(var key in strJson.data[0]){strRow='{"'+strDataTitle+'":"'+key+'"},';strHead+=strRow;}
strHead='['+strHead+']';return $.parseJSON(strHead);} 
function ReadData(strUrl,Type){var Data;if(Type){$.ajax({type:'GET',async:false,url:strUrl,success:function(data,status){var obj=jQuery.parseJSON(data);Data=obj;}});}else{$.ajax({type:'POST',async:false,url:strUrl,success:function(data,status){var obj=jQuery.parseJSON(data);Data=obj;}});}
return Data;}
function GetSelectStr(Data){var i,str='<option value=""></option>';for(i=0;i<Data.rows;i++){str+='<option value="'+Data.data[i][0]+'">'+Data.data[i][1]+'</option>';}
return str;}
function InitSelect(sel_Name,Data){$("select[name='"+sel_Name+"']").html(GetSelectStr(Data));} 
function SwitchSelect(Name,ID){var v='input:radio[name='+Name+']:nth('+ID+')';return v;} 
function GetSwitchValue(Name,Num){var v=$('input:radio[name='+Name+']:nth('+0+')').bootstrapSwitch('state');for(var i=1;i<=Num&&!v;i++){v=$('input:radio[name='+Name+']:nth('+i+')').bootstrapSwitch('state');}
return i;} 
function SetRadioChecked(Name,nID){$("input[name='"+Name+"']:nth("+nID+")").attr('checked',1);} 
function GetRadioChecked(Name){var i=0;for(i=0;i<$("input[name='"+Name+"']").length;i++){if($("input[name='"+Name+"']:nth("+i+")").attr('checked')=="checked"){return i;}}} 
