function getUrlParam(name){var reg=new RegExp("(^|&)"+name+"=([^&]*)(&|$)"); var r=window.location.search.substr(1).match(reg); if(r!==null)return unescape(r[2]);return null;}
function xround(x,num){x=parseFloat(x);return Math.round(x*Math.pow(10,num))/Math.pow(10,num);}
function UTF2GBK(str){return escape(str).toLocaleLowerCase().replace(/%u/gi,'\\u');}
function GBK2UTF(str){return unescape(str.replace(/\\u/gi,'%u'));}